import React, { useState } from 'react';

function EventForm({ event, setEvents, events, closeForm }) {
  const [title, setTitle] = useState(event.title || '');
  const [start, setStart] = useState(event.start);
  const [end, setEnd] = useState(event.end);

  const saveEvent = () => {
    const newEvent = {
      id: event.id || Date.now(),
      title,
      start: new Date(start),
      end: new Date(end)
    };

    if (event.id) {
      const updated = events.map(evt => (evt.id === event.id ? newEvent : evt));
      setEvents(updated);
    } else {
      setEvents([...events, newEvent]);
    }

    closeForm();
  };

  const deleteEvent = () => {
    const updated = events.filter(evt => evt.id !== event.id);
    setEvents(updated);
    closeForm();
  };

  return (
    <div className="event-form">
      <input value={title} onChange={e => setTitle(e.target.value)} placeholder="Event Title" />
      <input type="datetime-local" value={start.toISOString().slice(0,16)} onChange={e => setStart(new Date(e.target.value))} />
      <input type="datetime-local" value={end.toISOString().slice(0,16)} onChange={e => setEnd(new Date(e.target.value))} />
      <button onClick={saveEvent}>Save</button>
      {event.id && <button onClick={deleteEvent}>Delete</button>}
      <button onClick={closeForm}>Cancel</button>
    </div>
  );
}

export default EventForm;
