import React, { useState } from "react";
import { Calendar as BigCalendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import withDragAndDrop from "react-big-calendar/lib/addons/dragAndDrop";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "react-big-calendar/lib/addons/dragAndDrop/styles.css";
import EventForm from "./EventForm";
import CustomToolbar from "./CustomToolbar";

const localizer = momentLocalizer(moment);
const DnDCalendar = withDragAndDrop(BigCalendar);

function Calendar({ events, setEvents }) {
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [formOpen, setFormOpen] = useState(false);

  const moveEvent = ({ event, start, end }) => {
    const updated = events.map((evt) =>
      evt.id === event.id ? { ...evt, start, end } : evt
    );
    setEvents(updated);
  };

  const handleSelectSlot = ({ start }) => {
    const startDate = moment(start).startOf("day").toDate(); // normalized start
    const endDate = moment(start).endOf("day").toDate(); // end same day
    setSelectedEvent({ title: "", start: startDate, end: endDate });
    setFormOpen(true);
  };

  const handleSelectEvent = (event) => {
    setSelectedEvent(event);
    setFormOpen(true);
  };

  return (
    <div>
      <DnDCalendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 600 }}
        onEventDrop={moveEvent}
        selectable
        onSelectSlot={handleSelectSlot}
        onSelectEvent={handleSelectEvent}
        components={{
          toolbar: CustomToolbar, // ðŸ‘ˆ Add custom toolbar here
        }}
      />
      {formOpen && (
        <EventForm
          event={selectedEvent}
          setEvents={setEvents}
          events={events}
          closeForm={() => setFormOpen(false)}
        />
      )}
    </div>
  );
}

export default Calendar;
